// src/AlunoList.js
import React, { useState, useEffect } from 'react';

function AlunoList() {
    // Cria um "estado" para armazenar a lista de alunos que virá da API
    const [alunos, setAlunos] = useState([]);

    // useEffect é um gancho do React que executa o código quando o componente é montado na tela
    useEffect(() => {
        // Este é o "garçom" indo buscar o prato na cozinha!
        // Faz uma requisição GET para o endpoint que você criou no backend.
        fetch('http://localhost:8080/alunos')
            .then(response => response.json()) // Converte a resposta para JSON
            .then(data => setAlunos(data)) // Atualiza o estado 'alunos' com os dados recebidos
            .catch(error => console.error('Erro ao buscar alunos:', error)); // Lida com erros
    }, []); // O array vazio [] garante que isso só execute uma vez

    return (
        <div>
            <h1>Lista de Alunos</h1>
            {/* Se não houver alunos, mostra uma mensagem. Senão, cria uma lista. */}
            {alunos.length === 0 ? (
                <p>Nenhum aluno cadastrado ou aguardando dados...</p>
            ) : (
                <ul>
                    {/* Mapeia a lista de alunos para elementos <li> */}
                    {alunos.map(aluno => (
                        <li key={aluno.matricula}>
                            {aluno.nome} (Matrícula: {aluno.matricula})
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default AlunoList;
